import React from 'react'
import { useContext } from "react";
import { myContext } from "../context/main";
export default React.memo(function AddButton({id,quantity}) {
  const { itemCount, setItemCount, setAddedItem, addedItem } =
    useContext(myContext);
  const handleIncrement = (id) => {
    setItemCount(itemCount + 1);
    setAddedItem(addedItem =>{
        if(addedItem.find(item=>item.id === id) == null){
            return [...addedItem,{id:id,quantity:quantity,count:1}]
        }else{
           return addedItem.map((item)=>{
                if(item.id === id && item.quantity===quantity)
                    return {...item,count:item.count+1}
                else if(item.id === id && item.quantity!==quantity){
                    return {id:id,quantity:quantity,count:1};
                }
                else
                    return item;
            })
        }
    })
  };
  const handleDecrement = (id) => {
    setItemCount(itemCount - 1);
    setAddedItem((addedItem) => {
      if(addedItem.find(item=>item.id===id &&item.count === 1)){
        return addedItem.filter((item)=>item.id!==id);
      }
      return addedItem.map((item)=>{
        if (item.count > 0 && item.id === id)
          return { ...item, count: item.count - 1 };
        else return item;
      })
    });
  };
  return (addedItem.find((item) => item.id === id) !=
    null&&addedItem.find((item) => item.id === id).quantity) !==
    quantity || addedItem.find((item) => item.id === id) == null ? (
    <button
      type="button"
      class="btn btn-primary"
      onClick={() => handleIncrement(id)}
    >
      Add
    </button>
  ) : (
    <button type="button" class="btn btn-primary">
      <span onClick={() => handleIncrement(id)} style={{ paddingRight: "1px" }}>
        +
      </span>
      <span>
        {addedItem.find(
          (item) => item.id === id && item.quantity === quantity
        ) !== null &&
          addedItem.find((item) => item.id === id && item.quantity === quantity)
            .count}
      </span>
      <span onClick={() => handleDecrement(id)} style={{ paddingLeft: "1px" }}>
        -
      </span>
    </button>
  );
})
