import { useState } from "react";
import React from 'react'
import { useContext } from "react";
import { myContext } from "../context/main";
export default React.memo(function AddButton({id,quantity}) {
  console.log("button rerender");
  const [currItemCount, setCurrItemCount] = useState(0);
  const { itemCount, setItemCount,addedItem, setAddedItem } =
    useContext(myContext);
  const handleIncrement = () => {
    setCurrItemCount(currItemCount + 1);
    setItemCount(itemCount + 1);
    // const searchItem = addedItem.filter((item)=>{
    //     return id === item.id;
    // })
    setAddedItem([...addedItem,{id:id , quantity : quantity, currCount:currItemCount}])
  };
  const handleDecrement = () => {
    setCurrItemCount(currItemCount - 1);
    setItemCount(itemCount - 1);
    // const leftArray = addedItem.filter((item) => item.id !== id);
    // const searchArray = addedItem.filter((item) => item.id === id);
    // if(searchArray[0])
    setAddedItem([
      ...addedItem,
      { id: id, quantity: quantity, currCount: currItemCount },
    ]);
  };
  return currItemCount === 0 ? (
    <button type="button" class="btn btn-primary" onClick={handleIncrement}>
      Add
    </button>
  ) : (
    <button type="button" class="btn btn-primary">
      <span onClick={handleIncrement} style={{ paddingRight: "1px" }}>
        +
      </span>
      <span>{currItemCount}</span>
      <span onClick={handleDecrement} style={{ paddingLeft: "1px" }}>
        -
      </span>
    </button>
  );
})
