import React, { useContext, useState } from "react";
import AddButton from "../page/AddButton";
import { myContext } from "../context/main";
export default React.memo(function Card(props) {
  const foodCategory = props.foodCategory;
  const foodData = props.foodData;
  const [searchValue, setSearchValue] = useState("");
  const [selectOption,setSelectOption] = useState("");
  const {itemCount,setItemCount,addedItem,setAddedItem} = useContext(myContext);
  const [currItemCount, setCurrItemCount] = useState(0);
  const handleIncrement = ()=>{
    setCurrItemCount(currItemCount+1);
    setItemCount(itemCount+1);
    // const itemObj = {
    //   id : id,
    //   quantity : selectOption
    // }
    // setAddedItem([...addedItem, itemObj]);
  }
  const handleDecrement = ()=>{
    setCurrItemCount(currItemCount-1);
    setItemCount(itemCount-1);
  }
  const handleSelectOption = (e)=>{
    setSelectOption(e.target.value);
  }
  return (
    <div className="container">
      <div className="w-100 d-flex justify-content-end">
      <form
        className="w-50 searchbar mt-5 mb-3 pe-2"
        role="search"
      >
        <input
          className="form-control"
          type="search"
          value={searchValue}
          onChange={(e) => setSearchValue(e.target.value)}
          placeholder="Search the dish you want to have....."
          aria-label="Search"
        />
      </form>
      </div>
      {foodCategory.map((catagoryData) => {
        return (
          <>
            {searchValue.length === 0 && (
              <div className="ps-1 pe-2 border-bottom border-white mt-2 mb-5 fs-4 fst-italic">
                {catagoryData.CategoryName}
              </div>
            )}
            <div className="row row-cols-4">
              {foodData
                .filter(
                  (item) =>
                    item.CategoryName === catagoryData.CategoryName &&
                    item.name
                      .toLowerCase()
                      .includes(searchValue.toLocaleLowerCase())
                )
                .map((fData) => {
                  return (
                    <div className="col" key={fData._id}>
                      <div
                        className="card mb-3"
                        key={fData._id}
                        style={{ width: "18rem" }}
                      >
                        <img
                          src={fData.img}
                          className="card-img-top"
                          style={{ objectFit: "cover", height: "200px" }}
                          alt="..."
                        />
                        <div className="card-body">
                          <h5 className="card-title ps-1">{fData.name}</h5>
                          <div className="d-flex align-items-center justify-content-between pe-1 ps-1">
                            <select
                              className="form-select w-75"
                              name="quantity"
                              onChange={handleSelectOption}
                              aria-label="Default select example"
                            >
                              {Object.keys(fData.options[0]).map(
                                (key, index) => {
                                  return (
                                    <option
                                      key={index}
                                      value={`${key}:${fData.options[0][key]}`}
                                    >
                                      {key} : {fData.options[0][key]}
                                    </option>
                                  );
                                }
                              )}
                            </select>
                            <AddButton id={fData._id} handleIncrement={handleIncrement} handleDecrement={handleDecrement} currItemCount={currItemCount}/>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
            </div>
          </>
        );
      })}
    </div>
  );
});
