import {React,useEffect, useState} from 'react'
import Navbar from '../components/Navbar'
import Carousal from './Carousal'
import Card from '../components/Card'

export default function Home() {
  const [scrollData, setScrollData] = useState(0);
  const [searchBar,setSearchBar] = useState(false);
  const [foodData, setFoodData] = useState({});
  const handleScroll = () => {
    setScrollData(window.scrollY);
  };
  const getData = async ()=>{
    const response = await fetch('http://localhost:4000/api/getFoodItem',{
      method:'POST',
      headers:{
        "Content-type":"application/json"
      }
    });
    if(!response){
      alert("error in loading the data..");
      return;
    }
    const responseData =await response.json();
    console.log(responseData)
    setFoodData({...foodData,responseData});
    console.log(foodData);
  }
  useEffect(()=>{
    getData();
  },[])
  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    handleScroll();
    if(scrollData >= 660){
      setSearchBar(true);
      document.getElementById('navbar').style.backgroundColor='rgba(51,51,51,1)';
    }else{
      setSearchBar(false);
      document.getElementById('navbar').style.backgroundColor='transparent';
    }
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, [scrollData]);
  return (
    <div>
      <Navbar searchBarActive={searchBar} />
      <Carousal />
      {/* <Card foodData={foodData}/> */}
    </div>
  );
}
