import { useState } from "react";
import React from 'react'
import { useContext } from "react";
import { myContext } from "../context/main";
export default React.memo(function AddButton({id,quantity}) {
  const [currItemCount, setCurrItemCount] = useState(0);
  const { itemCount, setItemCount, setAddedItem } =
    useContext(myContext);
  const handleIncrement = (id) => {
    setCurrItemCount(currItemCount + 1);
    setItemCount(itemCount + 1);
    setAddedItem(addedItem =>{
        if(addedItem.find(item=>item.id === id) == null){
            return [...addedItem,{id:id,quantity:quantity,itemCount:1}]
        }else{
            addedItem.map((item)=>{
                if(item.id === id)
                    return {...item, itemCount:itemCount+1};
                else
                    return item;
            })
        }
    })
  };
  const handleDecrement = (id) => {
    setCurrItemCount(currItemCount - 1);
    setItemCount(itemCount - 1);
    setAddedItem((addedItem) => {
      if(addedItem.find(item=>item.id===id && item.itemCount === 1)){
        return addedItem.filter((item)=>item.id!==id);
      }
      addedItem.map((item)=>{
        if(item.itemCount>0 && item.id === id)
            return {...item, itemCount:itemCount-1};
        else
            return item;
      })
    });
  };
  return currItemCount === 0 ? (
    <button type="button" class="btn btn-primary" onClick={handleIncrement}>
      Add
    </button>
  ) : (
    <button type="button" class="btn btn-primary">
      <span onClick={handleIncrement} style={{ paddingRight: "1px" }}>
        +
      </span>
      <span>{currItemCount}</span>
      <span onClick={handleDecrement} style={{ paddingLeft: "1px" }}>
        -
      </span>
    </button>
  );
})
